import{_ as s,c as r,j as e,a as t,o as l}from"./chunks/framework.HHeKTx91.js";const f=JSON.parse('{"title":"声笔简码扩展编码优化","description":"","frontmatter":{"title":"声笔简码扩展编码优化","date":"2019-10-03T15:10:38.000Z","tags":["声笔简码","扩展编码","优化"]},"headers":[],"relativePath":"posts/jmkzmsyh.md","filePath":"posts/jmkzmsyh.md"}'),n={name:"posts/jmkzmsyh.md"};function o(d,a,i,m,p,c){return l(),r("div",null,a[0]||(a[0]=[e("h2",{id:"问题",tabindex:"-1"},[t("问题 "),e("a",{class:"header-anchor",href:"#问题","aria-label":'Permalink to "问题"'},"​")],-1),e("p",null,"在用声笔简码输入单字和二字词时，常常会有很多重码，初学者往往通过翻页来查找需要的字词。他们对声笔简码不熟悉，键速很慢，翻页和查找所占的时间比例不大，不会感到什么不方便。当用户对声笔简码逐步熟悉后，就会觉得翻页和查找占的时间比例越来越大，而宁可使用扩展编码。",-1),e("p",null,"然而，此前的声笔简码使用扩展编码时，仅仅显示那些在第四码时第一页没有显示的字词。所以，必须先确定四码时候选窗口中确实没有想要的字词，再使用扩展编码。当候选项比较多时，眼睛的频繁扫视仍然是很费时间和精力的。",-1),e("h2",{id:"措施",tabindex:"-1"},[t("措施 "),e("a",{class:"header-anchor",href:"#措施","aria-label":'Permalink to "措施"'},"​")],-1),e("p",null,"对于熟手来说，只需要看编码窗口中的一项就行了。如果该项不是想要的字词，就直接追加两个笔画，基本上就没有重码选择的顾虑了。如果是嵌入显示的，用户只需要看插入光标处的内容即可。",-1),e("p",null,"本次的优化做到了在使用扩展编码进行重码过滤时，仅仅排除了第四码时显示的第一项，所以用户只需要判断这第一项是否是所需要的就行了，从而可以大大地加快用户的操作速度。",-1)]))}const u=s(n,[["render",o]]);export{f as __pageData,u as default};
