import{_ as s,c as a,j as t,o}from"./chunks/framework.hU1yMXPg.js";const m=JSON.parse('{"title":"声笔顶拼正式发布","description":"","frontmatter":{"title":"声笔顶拼正式发布","date":"2020-05-20T15:46:14.000Z","tags":["声笔","顶功","拼音","顶拼"]},"headers":[],"relativePath":"posts/sbdp.md","filePath":"posts/sbdp.md"}'),p={name:"posts/sbdp.md"};function n(r,e,d,l,i,c){return o(),a("div",null,e[0]||(e[0]=[t("p",null,"声笔顶拼经过长期的酝酿和实验探索，现在终于修成正果，得以正式发布了！",-1),t("p",null,"声笔顶拼是声笔系列码中与传统拼音输入法最接近的一款输入法，其目的是让现有的拼音输入法用户以最小的学习代价就能大大地提升输入的效率，是拼音输入法提速的捷径。它通过对汉语拼音的简化和巧妙的字词编码，在保持易学的前提下，既能充分利用简拼来缩短词组的码长，又能通过追加韵母来延长编码，还能用 a、e、u、i、o 代表折、横、撇、竖、捺直接追加笔画来筛选重码字词。",-1),t("p",null,"同时，字词的编码自带了明确的边界，当后接声母时，可以将前面的字词顶上屏幕而省略空格，从而大大提高输入效率。在码长短的时候采用静态编码，易于让常见字词的输入形成条件反射；在码长长的时候进行动态码长调整，有利于缩短平均码长。采用 a、e、u、i、o 选择重码字词，能避免使用不便击打的数字键。",-1)]))}const _=s(p,[["render",n]]);export{m as __pageData,_ as default};
